					formulas.Source = new BitmapImage(new Uri(System.IO.Path.Combine(System.IO.Path.GetDirectoryName(System.Reflection.Assembly.GetExecutingAssembly().Location), "Images/formulas.jpg")));
					InlineUIContainer cont = new InlineUIContainer(formulas);
